import styled, { css } from 'styled-components'
import { mainBackground } from 'styles/OftenStyle'

export const SidebarDiv = styled.div<{ themeMode: string }>`
    ${({ theme, themeMode }) => {
        const { colors, fonts, mainStyle } = theme
        return css`
            z-index: 1003;
            position: absolute;
            top: 0;
            left: 10px;
            width: 80px;
            margin: 20px 0 20px 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
            height: 95%;
            min-height: 1000px;
            background: ${colors.sidebarBg};
            box-shadow: ${mainStyle.mainShadow};
            backdrop-filter: blur(54px);
            border-radius: 20px;

            .icons_wrap {
                display: flex;
                flex-direction: column;
                justify-content: space-between;
                height: 100%;
            }
            .bottom-icons {
                li {
                    width: 40px;
                    height: 40px;
                    border-radius: 100%;
                    ${mainBackground}
                    line-height: 40px;

                    .nav-icon {
                        margin: 0 auto;
                        height: 40px;
                    }
                }
            }

            ul {
                display: flex;
                flex-direction: column;
                border-radius: 10px;
                box-shadow: 0 10px 50px 0 rgba(#05043e, 0.25);
                margin-top: 100px;
                li:hover {
                    .hover-popup {
                        display: flex;
                        transform: scale(1);
                        opacity: 1;
                        /* transition: 0.35s ease; */
                    }
                }
                li {
                    display: flex;
                    position: relative;
                    margin: 0 0 20px 0;
                    cursor: pointer;
                    .nav-icon {
                        margin: 0 auto;
                        svg path {
                            fill: ${colors.white};
                        }
                    }
                    span {
                        margin: 4px auto;
                    }

                    .hover-popup {
                        /* display: flex; */
                        display: none;
                        position: absolute;
                        top: -11px;
                        left: -8px;
                        width: 172px;
                        height: 60px;
                        white-space: nowrap;
                        transform-origin: center left;
                        transform: scale(1);
                        opacity: 0;
                        transition: 0.35s ease;
                        background: ${colors.sidebarBg};
                        box-shadow: ${mainStyle.mainShadow};
                        backdrop-filter: ${mainStyle.mainFilter};
                        border-radius: 99px;

                        .hover-icon {
                            background: transparent;
                            position: absolute;
                            top: 10px;
                            left: 18px;
                            width: 50px;
                            height: 40px;
                            line-height: 60px;
                            svg {
                                line-height: 60px;
                            }
                        }

                        p {
                            font-size: ${fonts.f16};
                            font-weight: bold;
                            line-height: 60px;
                            color: ${colors.white};
                            text-align: center;
                            margin-left: 50px;
                        }
                    }
                }
            }
        `
    }}
`
