import styled, { css } from 'styled-components'
import { mainBackground } from 'styles/OftenStyle'

export const SidebarDiv = styled.div<{ themeMode: string }>`
    ${({ theme, themeMode }) => {
        const { colors, fonts, mainStyle } = theme
        return css`
            z-index: 1003;
            position: absolute;
            top: 0;
            left: 10px;
            margin: 20px 0 20px 20px;
            width: 80px;
            height: 80%;
            /* min-height: 1000px; */
            background: ${colors.sidebarBg};
            box-shadow: ${mainStyle.mainShadow};
            backdrop-filter: blur(54px);
            border-radius: 20px;

            .sidebar_wrap {
                display: flex;
                flex-direction: column;
                /* height: 100%; */
            }

            ul {
                display: flex;
                flex-direction: column;
                border-radius: 10px;
                box-shadow: 0 10px 50px 0 rgba(#05043e, 0.25);
                margin-top: 100px;

                li {
                    display: flex;
                    position: relative;
                    margin: 0 0 20px 0;
                    cursor: pointer;
                    p {
                        font-size: ${fonts.f14};
                    }
                }
            }

            // 다크모드 아이콘
            .bottom-icons {
                li {
                    width: 40px;
                    height: 40px;
                    border-radius: 100%;
                    ${mainBackground}
                    line-height: 40px;

                    .nav-icon {
                        margin: 0 auto;
                        height: 40px;
                    }
                }
            }
        `
    }}
`
