import React from 'react'
import { ServiceStatusCircleLi } from 'components/atoms/ServiceStatusCircle/ServiceStatusCircle.style'
import { DefaultTheme } from 'styled-components'

// 지도위에 있는 마커 팝업에도 해당 컴포넌트가 들어가는데, leaflet 라이브러리에 포함된 마커 팝업을 커스텀한거라
// 전역으로 설정해준 theme이 적용되지 않는 문제가 있어서 props로 theme을 내려줌
export interface IProps {
    theme?: DefaultTheme
    subscription_name: string
    status: string
    circle: boolean
}
function ServiceStatusCircle({ theme, subscription_name, status, circle }: IProps) {
    return (
        <ServiceStatusCircleLi status={status} theme={theme} circle={circle}>
            {/* 마커 팝업 위에서 onClick이벤트가 작동하지 않아 a 태그로 처리 */}
            {subscription_name === 'PVMS' && (
                <a href="https://pvms.sngy.io/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {subscription_name === 'FEMS' && (
                <a href="https://pnms.sngy.io/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {subscription_name === 'DRMS' && (
                <a href="https://drms.sngy.io/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {subscription_name === 'EE' && (
                <a href="http://34.97.109.59:3000/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {subscription_name === 'ESS' && (
                <a href="http://34.97.109.59:3000/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {subscription_name === 'CVR' && (
                <a href="http://34.97.109.59:3000/" target="_blank" rel="noreferrer">
                    {subscription_name}
                </a>
            )}
            {/* TODO - 링크 추가 (ESS, CVR) */}
        </ServiceStatusCircleLi>
    )
}

// export default ServiceStatusCircle
export default React.memo(ServiceStatusCircle)
